mapper.update:

UPDATE TODO
SET NO=#{no}
	, TITLE=#{title}
	, COMPLETE=#{complete}
			WHERE NO=#{no}

mapper.select:
SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
				SELECT
								NO
								, TITLE
								, COMPLETE
						FROM TODO
				WHERE 1=1

					AND
						NO = #{searchKeyword}

					AND
						TITLE LIKE '%' || #{searchKeyword} || '%'


					ORDER BY 
						NO DESC


	) A WHERE ROWNUM <= #{lastIndex}
)WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM TODO WHERE 1=1
AND NO = #{searchKeyword}
AND TITLE LIKE '%' || #{searchKeyword} || '%'
